targets.expr:
sum(
  (
    (
      sum(kube_node_status_capacity_cpu_cores{environment="$env"}) by (node)
       * on (node) group_left (label_cloud_google_com_gke_preemptible)
      kube_node_labels{label_cloud_google_com_gke_preemptible="true",environment="$env"}
    ) * $costpcpu
  )
  or
  (
    (
      sum(kube_node_status_capacity_cpu_cores{environment="$env"}) by (node)
       * on (node) group_left (label_cloud_google_com_gke_preemptible)
      kube_node_labels{label_cloud_google_com_gke_preemptible!="true",environment="$env"}
    ) * ($costcpu - ($costcpu / 100 * $costDiscount))
  )
)

sum(
  (
    (
      sum(kube_node_status_capacity_memory_bytes{environment="$env"}) by (node)
       * on (node) group_left (label_cloud_google_com_gke_preemptible)
      kube_node_labels{label_cloud_google_com_gke_preemptible="true",environment="$env"}
    ) /1024/1024/1024 * $costpram
  )
  or
  (
    (
      sum(kube_node_status_capacity_memory_bytes{environment="$env"}) by (node)
       * on (node) group_left (label_cloud_google_com_gke_preemptible)
      kube_node_labels{label_cloud_google_com_gke_preemptible!="true",environment="$env"}
    ) /1024/1024/1024 * ($costram - ($costram / 100 * $costDiscount))
)
)

# CPU
sum(
  (
    (
      sum(kube_node_status_capacity_cpu_cores{environment="$env"}) by (node)
       * on (node) group_left (label_cloud_google_com_gke_preemptible)
      kube_node_labels{label_cloud_google_com_gke_preemptible="true",environment="$env"}
    ) * $costpcpu
  )
  or
  (
    (
      sum(kube_node_status_capacity_cpu_cores{environment="$env"}) by (node)
       * on (node) group_left (label_cloud_google_com_gke_preemptible)
      kube_node_labels{label_cloud_google_com_gke_preemptible!="true",environment="$env"}
    ) * ($costcpu - ($costcpu / 100 * $costDiscount))
  )
) 

+ 

# Storage
sum (
  sum(kube_persistentvolumeclaim_info{storageclass=~".*ssd.*",environment="$env"}) by (persistentvolumeclaim, namespace, storageclass)
  + on (persistentvolumeclaim, namespace) group_right(storageclass)
  sum(kube_persistentvolumeclaim_resource_requests_storage_bytes{environment="$env"}) by (persistentvolumeclaim, namespace)
) / 1024 / 1024 /1024 * $costStorageSSD

+

sum (
  sum(kube_persistentvolumeclaim_info{storageclass!~".*ssd.*",environment="$env"}) by (persistentvolumeclaim, namespace, storageclass)
  + on (persistentvolumeclaim, namespace) group_right(storageclass)
  sum(kube_persistentvolumeclaim_resource_requests_storage_bytes{environment="$env"}) by (persistentvolumeclaim, namespace)
) / 1024 / 1024 /1024 * $costStorageStandard

+ 

sum(container_fs_limit_bytes{device=~"^/dev/[sv]d[a-z][1-9]$",id="/"}) / 1024 / 1024 / 1024 * $costStorageStandard

+ 
# RAM
sum(
  (
    (
      sum(kube_node_status_capacity_memory_bytes{environment="$env"}) by (node)
       * on (node) group_left (label_cloud_google_com_gke_preemptible)
      kube_node_labels{label_cloud_google_com_gke_preemptible="true",environment="$env"}
    ) /1024/1024/1024 * $costpram
  )
  or
  (
    (
      sum(kube_node_status_capacity_memory_bytes{environment="$env"}) by (node)
       * on (node) group_left (label_cloud_google_com_gke_preemptible)
      kube_node_labels{label_cloud_google_com_gke_preemptible!="true",environment="$env"}
    ) /1024/1024/1024 * ($costram - ($costram / 100 * $costDiscount))
)
)
